mapper.select:
SELECT * FROM
(


    SELECT * FROM project
    WHERE dep_id = #{depId}




        union

    SELECT * FROM project
    WHERE cou_id = #{couId}




        union

    SELECT * FROM project
    WHERE u_id = #{uId}



) AS pro WHERE TRUE

    and pro_status != 5


    and pro_name like "%"#{proName}"%"


    and town_id = #{townId}


    and prc_id = #{prcId}


    and inf_id = #{infId}


    and pro_status = #{proStatus}


and pro_date between #{beginTime} and #{endTime}



        AND IFNULL(pro_dis_start, '2222-12-22') > CURRENT_DATE


        AND pro_dis_start <= pro_dis_complete AND IFNULL(pro_dis_complete, '2222-12-22') > CURRENT_DATE


        AND IFNULL(pro_dis_complete, '2222-12-22') <= CURRENT_DATE

SELECT * FROM
project WHERE TRUE

    and dep_id = #{depId}


    and pro_name like "%"#{proName}"%"


    and town_id = #{townId}


    and prc_id = #{prcId}


    and inf_id = #{infId}

and pro_status = 2

and pro_date between #{beginTime} and #{endTime}



        AND IFNULL(pro_dis_start, '2222-12-22') > CURRENT_DATE


        AND pro_dis_start <= pro_dis_complete AND IFNULL(pro_dis_complete, '2222-12-22') > CURRENT_DATE


        AND IFNULL(pro_dis_complete, '2222-12-22') <= CURRENT_DATE

SELECT * FROM
project WHERE TRUE

    and dep_id = #{depId}


    and pro_name like "%"#{proName}"%"


    and town_id = #{townId}


    and prc_id = #{prcId}


    and inf_id = #{infId}

and pro_status = 4

and pro_date between #{beginTime} and #{endTime}



        AND IFNULL(pro_dis_start, '2222-12-22') > CURRENT_DATE


        AND pro_dis_start <= pro_dis_complete AND IFNULL(pro_dis_complete, '2222-12-22') > CURRENT_DATE


        AND IFNULL(pro_dis_complete, '2222-12-22') <= CURRENT_DATE

SELECT * FROM
(


    SELECT * FROM project
    WHERE dep_id = #{depId}




        union

    SELECT * FROM project
    WHERE cou_id = #{couId}




        union

    SELECT * FROM project
    WHERE u_id = #{uId}


) AS pro WHERE pro_status = 0

and pro_date between #{beginTime} and #{endTime}



        AND IFNULL(pro_dis_start, '2222-12-22') > CURRENT_DATE


        AND pro_dis_start <= pro_dis_complete AND IFNULL(pro_dis_complete, '2222-12-22') > CURRENT_DATE


        AND IFNULL(pro_dis_complete, '2222-12-22') <= CURRENT_DATE

SELECT COUNT(1) FROM
(


    SELECT pro_id FROM project
    WHERE dep_id = #{depId}
      AND pro_status = ${proStatus}
      AND pro_dis_complete IS NULL




        UNION

    SELECT pro_id FROM project
    WHERE cou_id = #{couId}
      AND pro_status = ${proStatus}
      AND pro_dis_complete IS NULL




        UNION

    SELECT pro_id FROM project
    WHERE u_id = #{uId}
      AND pro_status = ${proStatus}
      AND pro_dis_complete IS NULL


) p
